import React from "react"


function FirstPage(props){
  console.log(props)
  return (<div className="container">

      <div className="text"><div className="title">ONBOARDING</div>Con questo progetto imparerai 
      alcune delle funzionalità avanzate del css
      e inizierai ad utilizzare javascript per
      far prendere vita alla web app.</div>
      <div className="image"></div>
      <div className="dots"></div>
      <Button numPage={props.page} setPage ={props.changePage}></Button>
  </div>)
}

function SecondPage(props){
  return (<div className="container">

  <div className="text"><div className="title">ONBOARDING</div>Con questo progetto imparerai 
  alcune delle funzionalità avanzate del cssA
  e inizierai ad utilizzare javascript per
  far prendere vita alla web app.</div>
  <div className="image"></div>
  <div className="dots"></div>
  <Button numPage={props.page} setPage ={props.changePage}></Button>
</div>)
}

function ThirdPage(props){

}

function Button(props){
  // console.log(props.numPage)
  const changePage = () => {
    let pageN = (props.numPage)
    console.log(pageN);
    console.log(props.numPage)
    if(pageN===0){
      props.setPage(1);
    }if(pageN ===1){
      props.setPage(2);
    }if(props.setPage===2){
      props.setPage(0)
    }
  }
  return (
    <div className = "next-button" onClick={changePage} ><div className="text-button" >Avanti</div></div>
  )
}



function App() {
  const [page, setPage] = React.useState(0)
  // console.log(page);
  switch (page) {
    case 0:
      return (
        <div className="App">
         <FirstPage page = {page} changePage={setPage}></FirstPage>
        </div>
      );
    case 1:
      return(
      <div className="App">
        <SecondPage page = {page} changePage={setPage}></SecondPage>
      </div>)
    case 2:
      return(
      <div className="App">
        <ThirdPage page = {page} changePage={setPage}></ThirdPage>
      </div>)
    default:
      return(
      <h1 style={{
      color:'red', 
      textAlign:'center', 
      }}>Impossible to reach!</h1>
      )
  }

}

export default App;
