import React from "react"


function FirstPage(props){
  console.log(props)
  return (<div className="container">

      <div className="text"><div className="title">ONBOARDING</div>Con questo progetto imparerai 
      alcune delle funzionalit√† avanzate del css
      e inizierai ad utilizzare javascript per
      far prendere vita alla web app.</div>
      <div className="image"></div>
      <div className="dots"></div>
      <Button selectPage = {props.setPage} numPage={props.page}></Button>
  </div>)
}

function SecondPage(){

}

function ThirdPage(){

}

function Button(props){
  return (
    <div className = "next-button" onClick={() => props.selectPage(props.numPage)} ><div className="text-button" >Avanti</div></div>
  )
}



function App() {
  const [page, setPage] = React.useState(0)
  const changePage = (page) => {
    let pageN = (page+1)%3 
    switch (page) {
      case (pageN === 0):
        setPage(1);
        break;
      case (pageN === 1):
        setPage(2);
        break;
      case (pageN === 2):
        setPage(0);
        break;
      default:
        break;
    }
  }
  switch (page) {
    case 0:
      return (
        <div className="App">
         <FirstPage page = {page} setPage = {changePage}></FirstPage>
        </div>
      );
    case 1:
      return(
      <div className="App">
        <h1>Ciao</h1>
        <SecondPage page = {page} setPage = {changePage}></SecondPage>
      </div>)
    case 2:
      return(
      <div className="App">
        <ThirdPage page = {page} setPage = {changePage}></ThirdPage>
      </div>)
      break;
    default:
      return(
      <h1 style={{
      color:'red', 
      textAlign:'center', 
      }}>Impossible to reach!</h1>
      )
  }

}

export default App;
