import React from "react"


function FirstPage(props){
  console.log(props)
  return (<div className="container">

      <div className="text"><div className="title">ONBOARDING</div>Con questo progetto imparerai 
      alcune delle funzionalit√† avanzate del css
      e inizierai ad utilizzare javascript per
      far prendere vita alla web app.</div>
      <div className="image"></div>
      <div className="dots"></div>
      <Button numPage={props.page} setPage ={props.setPage}></Button>
  </div>)
}

function SecondPage(){
  return null;
}

function ThirdPage(){

}

function Button(props){
  console.log(props.numPage)
  const changePage = (page) => {
    let pageN = (props.numPage +1)%3 
    switch (props.numPagepage) {
      case (pageN === 0):
        props.setPage(1);
        break;
      case (pageN === 1):
        props.setPage(2);
        break;
      case (pageN === 2):
        props.setPage(0);
        break;
      default:
        break;
    }
  }
  const incrementPage = () => {
    props.setPage(props.numPage+1);
  }
  return (
    <div className = "next-button" onClick={incrementPage} ><div className="text-button" >Avanti</div></div>
  )
}



function App() {
  const [page, setPage] = React.useState(0)
  switch (page) {
    case 0:
      return (
        <div className="App">
         <FirstPage page = {page} changePage={setPage}></FirstPage>
        </div>
      );
    case 1:
      return(
      <div className="App">
        <h1>Ciao</h1>
        <SecondPage page = {page} changePage={setPage}></SecondPage>
      </div>)
    case 2:
      return(
      <div className="App">
        <ThirdPage page = {page} changePage={setPage}></ThirdPage>
      </div>)
    default:
      return(
      <h1 style={{
      color:'red', 
      textAlign:'center', 
      }}>Impossible to reach!</h1>
      )
  }

}

export default App;
